package zoho;

public class Bitwiseoperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		boolean a=true;
		boolean b = false;
		int c=4;
		//bitwise operator
		System.out.println(a&b);
		System.out.println(a|b);
		System.out.println(a^b);
		System.out.println(~c);
		System.out.println(c<<2);
		System.out.println(c>>2);
		System.out.println(c>>>2);

	}

}
