package zoho;

public class Concentric {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int r1=3;
		int r2=4;
		double area1=0;
		double area2=0;
		double area=0.0;
		area1=(3.14*r1*r1);
		area2=(3.14*r2*r2);
		area=area2-area1;
		System.out.println(area);

	}

}
